mapper.select:
SELECT COUNT(DISTINCT ti.id) FROM travel_info ti
WHERE 1=1
AND EXISTS ( SELECT 1 FROM travel_info_theme tit WHERE tit.travel_info_id = ti.id AND tit.theme_id IN
#{theme}
)
AND (ti.title LIKE CONCAT('%', #{search}, '%') OR ti.simple_description LIKE CONCAT('%', #{search}, '%'))

SELECT travel_info_id
FROM travel_info_like
WHERE user_id = #{userId}

SELECT COUNT(*) > 0
FROM travel_info_like
WHERE travel_info_id = #{travelInfoId} AND user_id = #{userId}

SELECT COUNT(*) > 0
FROM travel_info
WHERE id = #{travelInfoId}

SELECT COUNT(*) > 0
FROM group_user
WHERE group_type = 'TRAVELINFO'
AND group_id = #{travelInfoId}
AND user_id = #{userId}
