mapper.select:
SELECT COUNT(*)
FROM badge_quest bq
JOIN quest_user qu ON bq.quest_id = qu.quest_id
WHERE bq.badge_id = #{badge_id} 
AND qu.user_id = #{user_id} 
AND qu.status = 'COMPLETED'

mapper.update:
UPDATE badge_user 
SET is_displayed = #{is_displayed}

    , pinned_at = #{pinned_at}

WHERE user_id = #{user_id}

    AND badge_id = #{badge_id}

mapper.select:
SELECT COUNT(*) 
FROM quest_user qu
JOIN badge_quest bq ON bq.quest_id = qu.quest_id
WHERE bq.badge_id = #{badge_id}
AND qu.user_id = #{user_id}
AND qu.status = 'COMPLETED'

SELECT COUNT(*)
FROM badge_user bu
WHERE bu.badge_id = #{badge_id}

SELECT COUNT(*) > 0
FROM badge
WHERE id = #{badge_id}

SELECT COUNT(*) > 0
FROM quest
WHERE id = #{quest_id}

mapper.update:
UPDATE badge
SET 
    kor_title = #{request.kor_title},
    eng_title = #{request.eng_title},
    description = #{request.description}

        , icon = #{request.icon_url}

WHERE id = #{badge_id}

mapper.insert:
INSERT INTO badge_quest (
    badge_id,
    quest_id
) VALUES (
    #{badgeId},
    #{questId}
)
